mapper.insert:
INSERT INTO community_board(
				community_board_tag
				, community_board_content
				, community_board_date
				, member_id
				, community_board_img
			)
VALUES(
	#{communityBoardTag}
	, #{communityBoardContent}
	, now()
	, #{memberId}
	, #{communityBoardImg}
)

INSERT INTO file(
				original_name
				, file_name
				, file_path
				, member_id
			)
VALUES(
	#{originalName}
	, #{fileName}
	, #{filePath}
	, #{memberId}
)

mapper.update:
UPDATE	community_board
SET		community_board_tag=#{communityBoardTag}
		, community_board_content=#{communityBoardContent}
WHERE	community_board_id=#{communityBoardId}
