mapper.insert:
		INSERT INTO recommanding(
                        recommanding_book_time
                        , recommanding_title
                        , recommanding_book_content
                        , recommanding_agree
                        , recommanding_book_no1
                        , recommanding_tag
                        , book_isbn1
                        , book_isbn2
                        , book_isbn3
                        , book_isbn4
                        , book_isbn5
                        , member_id
                    )
        VALUES(
            now()
            , #{recommandingTitle}
            , #{recommandingBookContent}
            , #{recommandingAgree}
            , #{recommandingBookNo1}
            , #{recommandingTag}
            , #{bookIsbn1}
            , #{bookIsbn2}
            , #{bookIsbn3}
            , #{bookIsbn4}
            , #{bookIsbn5}
            , #{memberId}
        )

mapper.update:
		UPDATE	recommanding
