mapper.select:
SELECT  *
FROM    user
WHERE   member_name=#{memberName}
        AND member_tel=#{memberTel}
        
            AND member_id=#{memberId}

SELECT  count(*)
FROM    user
WHERE   member_id=#{memberId}

mapper.insert:
		INSERT INTO user(
						member_id
                        , member_password
                        , member_name
                        , member_email
                        , member_nickname
                        , member_gender
                        , member_birthday
                        , member_tel
                        , join_date
                        , quit_date
                        , member_visit_agree
                        , member_grade
                        , card_number
                        , card_cvc
                        , card_date
                        , card_password
                        , member_img
					)
		VALUES(
			#{memberId}
            , #{memberPassword}
            , #{memberName}
            , #{memberEmail}
            , #{memberNickname}
            , #{memberGender}
            , #{memberBirthday}
            , #{memberTel}
            , now()
            , '2030-12-31 00:00:00'
            , #{memberVisitAgree}
            , 'basic'
            , #{cardNumber}
            , #{cardCvc}
            , #{cardDate}
            , #{cardPassword}
            , #{memberImg}
		)

mapper.update:
		UPDATE	user
		SET		member_password=#{memberPassword}
                , member_name=#{memberName}
                , member_email=#{memberEmail}
                , member_nickname=#{memberNickname}
                , member_gender=#{memberGender}
                , member_tel=#{memberTel}
                , member_visit_agree=#{memberVisitAgree}
                , card_number=#{cardNumber}
                , card_cvc=#{cardCvc}
                , card_date=#{cardDate}
                , card_password=#{cardPassword}
		WHERE	member_id=#{memberId}

UPDATE  user
SET     member_password=#{tempPw}
WHERE   member_id=#{memberId}
